CREATE DATABASE  IF NOT EXISTS `week03_lab`;
USE `week03_lab`;
DROP TABLE IF EXISTS worldcountries;

CREATE TABLE worldcountries (
  `Code` text,
  `Name` text,
  Continent text,
  Region text,
  SurfaceArea int DEFAULT NULL,
  IndepYear text,
  Population int DEFAULT NULL,
  LifeExpectancy double DEFAULT NULL,
  GovernmentForm text,
  CapitalName text,
  CapitalPopulation int DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;


